			$comparison = $criteria->getComparison(UserSenderPeer::ID);
			$selectCriteria->add(UserSenderPeer::ID, $criteria->remove(UserSenderPeer::ID), $comparison);

		} else { // $values is UserSender object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

